            function ahah( url, target, callback ) {
                document.getElementById( target ).innerHTML = '<img src="mfm/loading.gif" alt="" />  echo $lng['loading']; ';
                if ( window.XMLHttpRequest ) {
                    req = new XMLHttpRequest( );
                } else if ( window.ActiveXObject ) {
                    req = new ActiveXObject( "Microsoft.XMLHTTP" );
                }
                if ( req != undefined ) {
                    req.onreadystatechange = function( ) {
                        ahahDone( url, target, callback );
                    };
                    var url = url + ( ( url.indexOf( "?" ) == -1 ) ? "?" : "&" ) + escape( new Date( ).toString( ) ); //prevent caching
                    req.open( "GET", url, true );
                    req.send( "" );
                }
            }

            function ahahDone( url, target, callback ) {
                if ( req.readyState == 4 ) {
                    if ( req.status == 200 ) {
                        document.getElementById( target ).innerHTML = req.responseText;
                        if ( callback )
                        {
                            callback( );
                        }
                        bindSearch( );
                    } else {
                        document.getElementById( target ).innerHTML = " AHAH Error:\n" + req.status + "\n" + req.statusText;
